import React, { Component } from "react";
class Streams extends Component {
  state = {
    data: "",
    url: ""
    //need to use regex probably to programatically replace -{width}x{height} with actual width and height property
  };

  _formatThumbnail = url => {
    console.log(url);
    url.replace("{width}", 400);
    url.replace("{height}", 200);
    console.log(url);
    this.setState({ url: url });
  };
  _getStreams = e => {
    e.preventDefault();
    fetch("https://api.twitch.tv/helix/streams?first=5&user_login=summit1g", {
      method: "GET",
      headers: {
        "Client-ID": "q4a4v5p2h4f78krp3cc088wln1u54j",
        "Content-Type": "application/json"
      }
    })
      .then(response => response.text())
      .then(result => {
        const parsedResult = JSON.parse(result);
        this.setState({ data: result });
        this._formatThumbnail(parsedResult.data[0].thumbnail_url);
        // var result = replace(sentence, "{.*?}", value);
        // this.setState({ url: parsedResult.data[0].thumbnail_url });
        console.log(parsedResult.data[0]);
      })
      .catch(error => console.log("ERROR: ", error));
  };

  render() {
    return (
      <div className="todoListMain">
        <div className="header">
          <button onClick={e => this._getStreams(e)}> get streams </button>
          <p>{this.state.data}</p>
          <p>{this.state.url.replace("{width}", 400)}</p>
          <img alt={"thumbnail"} src={this.state.url} />
        </div>
      </div>
    );
  }
}
export default Streams;
